from befree.api_model.config.headers import Headersfrom befree.api_model.catalog.internal.variations.endpoints import Endpointsfrom befree.api_model.catalog.internal.variations.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass Variations:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def get_stores_for_variations(self, fias_id, items):        response: Response = api.internal_session.post(            url=self._endpoints.get_stores_for_variations,            data=self._payloads.get_stores_for_variations(fias_id=fias_id, items=items),            headers=self._headers.common,        )        return response