import jsonclass Payloads:    def update(self, title, slug, **kwargs):        payload = {            "public_title": title,            "slug": slug,        }        payload.update(kwargs)        return json.dumps(payload)    def create(self, title, slug, **kwargs):        payload = {            "public_title": title,            "slug": slug,        }        payload.update(kwargs)        return json.dumps(payload)