class Payloads:    def update_frozen(self, frozen_type, article, is_delete):        payload = {            "products_array": article,            "is_delete": is_delete,            "_method": "PUT",        }        types = {}        for i in range(len(frozen_type)):            types[f"types[{i}]"] = frozen_type[i]        payload.update(types)        return payload    def update_frozen_with_file(self, frozen_type, is_delete):        payload = {            "is_delete": is_delete,            "_method": "PUT",        }        types = {}        for i in range(len(frozen_type)):            types[f"types[{i}]"] = frozen_type[i]        payload.update(types)        return payload    def files(self, products_file):        return {            "products_file": products_file,        }