from befree.api_model.config.headers import Headersfrom befree.api_model.catalog.private.products.endpoints import Endpointsfrom befree.api_model import apifrom requests import Responseclass Products:    def __init__(self):        self._endpoints = Endpoints()        self._headers = Headers()    def delete_variation(self, product_id, variation_id):        response: Response = api.private_session.delete(            url=self._endpoints.delete_variation(product_id=product_id, variation_id=variation_id),        )        return response    def patch_variation(self, product_id, variation_id):        response: Response = api.private_session.patch(            url=self._endpoints.delete_variation(product_id=product_id, variation_id=variation_id),        )        return response    def delete_product(self, product_id):        response: Response = api.private_session.delete(            url=self._endpoints.delete_product(product_id=product_id),        )        return response