from befree.api_model.config.headers import Headersfrom befree.api_model.catalog.private.stores.payloads import Payloadsfrom befree.api_model.catalog.private.stores.endpoints import Endpointsfrom befree.api_model import apifrom requests import Responseclass Stores:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def update_store(self, store_id, **kwargs):        response: Response = api.private_session.put(            url=self._endpoints.update_store(store_id=store_id),            data=self._payloads.update_store(**kwargs),            headers=self._headers.common        )        return response