from befree.api_model.config.headers import Headersfrom befree.api_model.catalog.public.product.endpoints import Endpointsfrom befree.api_model import apifrom requests import Responseclass Product:    def __init__(self):        self._endpoints = Endpoints()        self._headers = Headers()    def get(self, article, fias_id="c2deb16a-0330-4f05-821f-1d09c93331e6", golden_record="sank-823938"):        response: Response = api.public_session.get(            url=self._endpoints.get(article=article, fias_id=fias_id, golden_record=golden_record), headers=self._headers.common        )        return response