from voluptuous import PREVENT_EXTRA, ALLOW_EXTRA, Schema, Boolean, Email, Url, Anyattribute_composition_without_name = Schema(    [        {            "name": None,            "value": [str]        }    ],    extra=PREVENT_EXTRA,    required=True,)attribute_composition_with_name = Schema(    [        {            "name": str,            "value": [str]        }    ],    extra=PREVENT_EXTRA,    required=True,)attribute_composition_name = Schema(    [        {            "name": Any(None,str),            "value": [str]        }    ],    extra=PREVENT_EXTRA,    required=True,)