from befree.api_model import db_connectionclass QueriesConfig:    def get_config_by_key(self, key):        query = f"""                select parent_id , title , key ,type , value_int ,value_string ,value_text ,value_bool ,is_private, metadata                 from configs                where "key" = '{key}'           """        request = db_connection.config.get_data(query)        return request    def get_root_config_by_key(self, key):        query = f"""                select c1.title , c1.key ,c1.type, c1.value_int ,c1.value_string ,c1.value_text ,c1.value_bool ,c1.is_private                 from configs c1                join configs c2 on c2.id = c1.parent_id                where c2."key" = '{key}'           """        request = db_connection.config.get_data(query)        return request