from voluptuous import PREVENT_EXTRA, Schema, Booleanprivate_config = Schema(    {        "status": "OK",        "configs": [            {                "title": str,                "key": "inventory_rn_limit",                "value": int,                "is_private": True,                "metadata": {}            }        ]    },    extra=PREVENT_EXTRA,    required=True,)int_config = Schema(    {        "status": "OK",        "configs": [            {                "title": str,                "key": str,                "value": int,                "is_private": Boolean(),                "metadata": {}            }        ]    },    extra=PREVENT_EXTRA,    required=True,)str_config = Schema(    {        "status": "OK",        "configs": [            {                "title": str,                "key": str,                "value": str,                "is_private": Boolean(),                "metadata": {}            }        ]    },    extra=PREVENT_EXTRA,    required=True,)root_config = Schema(    {        "status": "OK",        "configs": [            {                "title": str,                "key": str,                "value": [                    {                        "title": str,                        "key": str,                        "value": Boolean(),                        "is_private": Boolean(),                        "metadata": {}                    }                ],                "is_private": Boolean(),                "metadata": {}            }        ]    },    extra=PREVENT_EXTRA,    required=True,)