import jsonclass Payloads:    def update_config(self, configs):        return json.dumps({"configs": configs})    def update_single(self, key, title, value, **kwargs):        if "metadata" in kwargs:            payload = {"configs": [{"key": key, "title": title, "value": value, "metadata": kwargs.pop("metadata")}]}        else:            payload = {"configs": [{"key": key, "title": title, "value": value}]}        return json.dumps(payload)    def update_root(self, root: {}, configs: []):        value = []        for i in range(len(configs)):            value.append({"key": configs[i]["key"], "title": configs[i]["title"], "value": configs[i]["value"]})        return json.dumps({"configs": [{"key": root["key"], "title": root["title"], "value": value}]})