from befree.api_model.esb.endpoints import Endpointsfrom befree.api_model.esb.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass Esb:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()    def esb_pickuppoints(self, products, total_item_price, golden_id="sank-823938",                         fulfillment_type="fulfillment"):        response: Response = api.esb_session.post(            url=self._endpoints.esb_pickuppoints,            data=self._payloads.esb_pickuppoints(golden_id=golden_id, products=products,                                                 total_item_price=total_item_price, fulfillment_type=fulfillment_type),        )        return response    def esb_intervals(self, products, total_item_price, address, golden_id="sank-823938",                      fulfillment_type="fulfillment"):        response: Response = api.esb_session.post(            url=self._endpoints.esb_intervals,            data=self._payloads.esb_intervals(golden_id=golden_id, products=products, total_item_price=total_item_price,                                              address=address, fulfillment_type=fulfillment_type),        )        return response    def esb_check_order(self, order_number):        response: Response = api.esb_session.get(            url=self._endpoints.esb_check_order(order_number=order_number),        )        return response    def esb_stocks(self, stores=["BF0000002"], **kwargs):        response: Response = api.esb_session.post(            url=self._endpoints.esb_stocks,            data=self._payloads.esb_stocks(stores=stores, **kwargs),        )        return response