import jsonclass Payloads:    def esb_pickuppoints(self, golden_id, products, total_item_price, fulfillment_type):        return json.dumps(            {                "cityId": golden_id,                "systemSettings": {                    "fulfillmentTypeId": fulfillment_type,                    "stockType": "ignore"                },                "products": products,                "totalItemPrice": total_item_price            }        )    def esb_intervals(self, golden_id, products, total_item_price, address, fulfillment_type):        return json.dumps(            {                "cityId": golden_id,                "systemSettings": {                    "fulfillmentTypeId": fulfillment_type,                    "stockType": "ignore"                },                "products": products,                "totalItemPrice": total_item_price,                "filterByLogisticGroup": True,                "calculateDaysLimit": 7,                "addressTo": address            }        )    def esb_stocks(self, stores, **kwargs):        payload = {            "brand": "befree",            "stores": stores,            "barcodes": [],            "rn": "1",            "top": "50000"        }        payload.update(kwargs)        return json.dumps(payload)