from befree.api_model.config.headers import Headersfrom befree.api_model.orders.private.employee_days.endpoints import Endpointsfrom befree.api_model.orders.private.employee_days.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass EmployeeDays:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def get_employee_days(self):        response: Response = api.orders_private_session.post(            url=self._endpoints.get_employee_days,            headers=self._headers.common,        )        return response    def create_employee_day(self, date):        response: Response = api.orders_private_session.post(            url=self._endpoints.create_employee_day,            data=self._payloads.create_employee_day(date=date),            headers=self._headers.common,        )        return response    def delete_employee_day(self, id):        response: Response = api.orders_private_session.post(            url=self._endpoints.delete_employee_day,            data=self._payloads.delete_employee_day(id=id),            headers=self._headers.common        )        return response