import jsonclass Payloads:    def list_unsent_orders(self, query, page, per_page):        return json.dumps({            "page": page,            "perPage": per_page,            "filter": {                "query": query            }        })    def list_receipts(self, query, withoutReceipt, page, per_page):        return json.dumps(            {                "page": page,                "perPage": per_page,                "filter": {                    "query": query,                    "withoutReceipt": withoutReceipt                }            }        )    def reprint_receipt(self, order_id):        return json.dumps({            "order": {                "id": order_id            }        })    def resend_order(self, order_id):        return json.dumps({            "order": {                "id": order_id            }        })    def cancel_order(self, order_id):        return json.dumps({            "order": {                "id": order_id            }        })