from voluptuous import PREVENT_EXTRA, ALLOW_EXTRA, Schema, Boolean, Email, Url, Anylist_unsent_orders = Schema(    {        "data": {            "orders": [                {                    "id": int,                    "number": str,                    "error": Any(None, {                        "status": int,                        "message": str                    }),                    "paymentMethod": Any("Payture", "СБП", "Подели", "При получении", "Сбер"),                    "total": int,                    "tempAt": str,                    "cancel": {                        "enabled": Boolean(),                    },                }            ],            "pagination": {"total": int, "perPage": int, "currentPage": int, "lastPage": int},        }    },    extra=ALLOW_EXTRA,    required=True,)list_receipts = Schema({    "data": {        "orders": [            {                "id": int,                "number": str,                "customer": {                    "id": int,                    "email": Email(),                },                "paymentMethod": Any("Payture", "СБП", "Подели", "Сбер"),                "total": int,                "tempAt": str,                "link": Any(None, Url()),                "receiptStatus": Any("notIssued", "processing", "received")            }        ],        "pagination": {            "total": int,            "perPage": int,            "currentPage": int,            "lastPage": int        }    }},    extra=PREVENT_EXTRA,    required=True,)