from befree.api_model.config.headers import Headersfrom befree.api_model.orders.private.pricerules.endpoints import Endpointsfrom befree.api_model.orders.private.pricerules.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass Pricerules:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def get_types(self):        response: Response = api.orders_private_session.post(            url=self._endpoints.get_types,            headers=self._headers.common        )        return response    def get_list(self, page=1, per_page=10, query="", types_ids=[], status=[], date_start=None, date_end=None,                 sort="id", sort_order="desc"):        response: Response = api.orders_private_session.post(            url=self._endpoints.get_list,            data=self._payloads.get_list(                page=page, per_page=per_page, query=query, types_ids=types_ids,                status=status, date_start=date_start, date_end=date_end,                sort=sort, sort_order=sort_order            ),            headers=self._headers.common        )        return response    def delete(self, pricerule_id):        response: Response = api.orders_private_session.post(            url=self._endpoints.delete(pricerule_id=pricerule_id),            headers=self._headers.common        )        return response    def get(self, pricerule_id):        response: Response = api.orders_private_session.post(            url=self._endpoints.get(pricerule_id=pricerule_id),            headers=self._headers.common        )        return response    def create(self, **kwargs):        response: Response = api.orders_private_session.post(            url=self._endpoints.create,            data=self._payloads.create(**kwargs),            headers=self._headers.common        )        return response    def update(self, pricerule_id, current_data, **kwargs):        response: Response = api.orders_private_session.post(            url=self._endpoints.update(pricerule_id=pricerule_id),            data=self._payloads.update(current_data=current_data, **kwargs),            headers=self._headers.common        )        return response