import jsonclass Payloads:    def get_delivery_methods(        self,        cart_uuid,        fias_id,        golden_record,    ):        return json.dumps(            {                "cartUuid": cart_uuid,                "cityData": {                    "fiasId": fias_id,                    "goldenRecord": golden_record,                },            }        )    def delivery_methods_availability(        self,        cart_uuid,        fias_id,        golden_record,    ):        return json.dumps(            {                "cityData": {                    "fiasId": fias_id,                    "goldenRecord": golden_record,                },                "cartUuid": cart_uuid,            }        )    def get_intervals(self, cart_uuid, fias_id, golden_record, address):        return json.dumps(            {                "cartUuid": cart_uuid,                "cityData": {                    "fiasId": fias_id,                    "goldenRecord": golden_record,                },                "address": address,            }        )    def get_pickpoints(self, cart_uuid, fias_id, golden_record, methods):        return json.dumps(            {                "cartUuid": cart_uuid,                "cityData": {                    "fiasId": fias_id,                    "goldenRecord": golden_record,                },                "methods": methods,            }        )