from voluptuous import PREVENT_EXTRA, ALLOW_EXTRA, Schema, Boolean, Email, Url, Anypickup = Schema(    {        "data": [            {                "id": str,                "externalId": str,                "name": str,                "description": Any(None, str),                "subwayStation": Any(None, str),                "workingHours": str,                "address": str,                "deliveryDate": str,                "deliveryPrice": int,                "method": Any("pickup", "reserveinstore", "pickupinstore"),                "coordinates": {"latitude": str, "longitude": str},                "items": Any(None, [{"variationId": int}]),                "services": Any(None, [str]),                "maxWaitingDays": int,            }        ]    },    extra=ALLOW_EXTRA,    required=True,)intervals = Schema(    {        "data": {            "services": [                {                    "name": str,                    "notifications": [],                    "dateFrom": str,                    "priceMin": int,                    "dates": [                        {                            "date": str,                            "intervals": [{"id": str, "date": str, "from": str, "to": str, "services": Any(None, [str]), "deliveryPrice": int}],                        }                    ],                }            ]        },        "status": str,    },    extra=ALLOW_EXTRA,    required=True,)