from befree.api_model.config.headers import Headersfrom befree.api_model.orders.public.geo.endpoints import Endpointsfrom befree.api_model.orders.public.geo.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass Geo:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def city_by_ip(self):        response: Response = api.orders_session.post(            url=self._endpoints.city_by_ip,            headers=self._headers.common,        )        return response    def suggestion_city(self, query):        response: Response = api.orders_session.post(            url=self._endpoints.suggestion_city,            data=self._payloads.suggestion_city(query=query),            headers=self._headers.common,        )        return response    def suggestion_street(self, city_fias_id, query):        response: Response = api.orders_session.post(            url=self._endpoints.suggestion_street,            data=self._payloads.suggestion_street(city_fias_id=city_fias_id, query=query),            headers=self._headers.common,        )        return response    def suggestion_building(self, street_fias_id, query):        response: Response = api.orders_session.post(            url=self._endpoints.suggestion_building,            data=self._payloads.suggestion_building(street_fias_id=street_fias_id, query=query),            headers=self._headers.common,        )        return response