import jsonclass Payloads:    def suggestion_city(self, query):        return json.dumps({            "query": query        })    def suggestion_street(self, city_fias_id, query):        return json.dumps({            "fiasId": city_fias_id,            "query": query        })    def suggestion_building(self, street_fias_id, query):        return json.dumps({            "fiasId": street_fias_id,            "query": query        })