from voluptuous import PREVENT_EXTRA, Schema, Booleancity_by_ip = Schema(    {        "data": {            "name": str,            "fullName": str,            "cityData": {                "fiasId": str,                "goldenRecord": str            },            "defined": Boolean(),            "befreeCityId": str        },        "status": str    },    extra=PREVENT_EXTRA,    required=True)suggestion_city = Schema(    {        "data": [            {                "name": str,                "fullName": str,                "cityData": {                    "fiasId": str,                    "goldenRecord": str                },                "befreeCityId": str            }        ],        "status": str    },    extra=PREVENT_EXTRA,    required=True)suggestion_street = Schema(    {        "data": [            {                "name": str,                "fiasId": str            }        ],        "status": str    },    extra=PREVENT_EXTRA,    required=True)suggestion_building = Schema(    {        "data": [            {                "building": str,                "fiasId": str,                "zipcode": str,                "coordinates": {                    "latitude": str,                    "longitude": str                }            }        ],        "status": str    },    extra=PREVENT_EXTRA,    required=True)