from befree.api_model.config.headers import Headersfrom befree.api_model.orders.public.order.endpoints import Endpointsfrom befree.api_model.orders.public.order.payloads import Payloadsfrom befree.api_model import apifrom requests import Responseclass Order:    def __init__(self):        self._endpoints = Endpoints()        self._payloads = Payloads()        self._headers = Headers()    def send_order(        self, cart_uuid, fias_id="c2deb16a-0330-4f05-821f-1d09c93331e6", golden_record="sank-823938", endpoint_id="Befree", platform="DESKTOP"    ):        response: Response = api.orders_session.post(            url=self._endpoints.send_order,            data=self._payloads.send_order(                cart_uuid=cart_uuid, fias_id=fias_id, golden_record=golden_record, endpoint_id=endpoint_id, platform=platform            ),            headers=self._headers.common,        )        return response    def get_order(self, cart_uuid, order_id):        response: Response = api.orders_session.post(            url=self._endpoints.get_order,            data=self._payloads.get_order(cart_uuid=cart_uuid, order_id=order_id),            headers=self._headers.common,        )        return response    def get_history(self, **kwargs):        token = dict()        token["Authorization"] = f"Bearer {kwargs.pop('token') if 'token' in kwargs else ''}"        response: Response = api.orders_session.post(url=self._endpoints.get_orders_history, headers={**self._headers.common, **token})        return response