import jsonclass Payloads:    def send_order(self, cart_uuid, fias_id, golden_record, endpoint_id, platform, **kwargs):        payload = {            "cartUuid": cart_uuid,            "cityData": {                "fiasId": fias_id,                "goldenRecord": golden_record,            },            "userEnvironment": {                "platform": platform,            },            "mindBox": {                "endpointId": endpoint_id,                "UUID": "fd399d03-24ba-43cc-a3e6-b23f34c4c944",            },        }        payload.update(kwargs)        return json.dumps(payload)    def get_order(self, cart_uuid, order_id):        return json.dumps({"cartUuid": cart_uuid, "orderId": order_id})