from playwright.sync_api import Pageclass Locators:    def __init__(self, page: Page):        self.page = page        self.burger = page.get_by_test_id("menu-burger-button")        self.logo = page.get_by_test_id("header-logo")        self.search_button = page.get_by_test_id("header-search-button")        self.search_input = page.get_by_test_id("header-search-input")        self.city_link = page.get_by_test_id("header-city-link")        self.profile_button = page.get_by_test_id("profile-button")        self.favorites_button = page.get_by_test_id("header-favorites-link")        self.cart_button = page.get_by_test_id("header-cart-link")        self.city = lambda city_name:page.get_by_text(city_name, exact=True)