class Locators:    def __init__(self, page):        # product        self.product_sizes = page.get_by_test_id("product-sizes")        self.product_size = lambda id: page.get_by_test_id(f"product-size-{id}")        self.product_children_sizes = page.locator('div[data-testid="product-sizes"] > *')        self.product_heights = page.get_by_test_id("size-table-heights")        self.product_height = lambda id: page.get_by_test_id(f"product-height-{id}")        self.product_stickers = page.get_by_test_id("product-stickers")        self.product_sticker = lambda id: page.get_by_test_id(f"product-sticker-{id}")        self.button_product_out_of_stock = page.get_by_test_id("product-button-out-of-stock")        self.button_product_out_of_stock_mobile = page.get_by_test_id("product-button-out-of-stock-mobile")        self.button_product_select_size = page.get_by_test_id("product-button-size")        self.button_product_select_height = page.get_by_test_id("product-button-height")        self.button_product_add_cart = page.get_by_test_id("product-button-add-cart")        self.button_product_add_cart_mobile = page.get_by_test_id("product-button-add-cart-mobile")        self.button_product_to_delivery = page.get_by_test_id("product-button-to-delivery")        self.button_product_go_cart = page.get_by_test_id("product-button-go-cart")        self.button_product_available_stores = page.get_by_test_id("product-button-available-stores")        self.link_product_available_stores = page.get_by_test_id("product-link-available-stores")        # size table        self.size_table = page.get_by_test_id("product-size-table")        self.size_table_sizes = page.get_by_test_id("size-table-sizes")        self.size_table_size = lambda id: page.get_by_test_id(f"size-table-size-{id}")        self.size_table_children_sizes = page.locator('div[data-testid="size-table-sizes"] > *')        self.size_table_heights = page.get_by_test_id("size-table-heights")        self.size_table_height = lambda id: page.get_by_test_id(f"size-table-height-{id}")        self.size_table_children_heights = page.locator('div[data-testid="size-table-heights"] > *')        self.height_info = page.get_by_test_id("height-info")        self.height_info_text = page.get_by_text(            "разница в длине между ростами — 5 сантиметров по внутреннему шву", exact=True        )        self.attributes_product_dimensions = page.get_by_test_id("attributes-product-dimensions")        self.attributes_body_dimensions = page.get_by_test_id("attributes-body-dimensions")        self.attributes_size_comparison = page.get_by_test_id("attributes-size-comparison")        self.button_size_table_out_of_stock = page.get_by_test_id("size-table-button-out-of-stock")        self.button_size_table_select_size = page.get_by_test_id("size-table-button-size")        self.button_size_table_select_height = page.get_by_test_id("size-table-button-height")        self.button_size_table_add_cart = page.get_by_test_id("size-table-button-add-cart")        self.button_size_table_to_delivery = page.get_by_test_id("size-table-button-to-delivery")        self.button_size_table_go_cart = page.get_by_test_id("size-table-button-go-cart")        self.description = page.get_by_test_id('product-description-text')        self.composition_and_care = page.get_by_test_id('product-description-constituents-and-care')        self.delivery_and_payment = page.get_by_test_id('product-description-delivery-and-payment')