import allurefrom befree.api_model.orders.private import OrdersPrivatefrom befree.api_model.orders.db_queries.queries import QueriesOrdersfrom befree.api_model.orders import utilsfrom allure_commons.types import Severityclass TestPricerulesDelete(OrdersPrivate, QueriesOrders):    @allure.id("2396")    @allure.title("Удаленный прайсрул не выводится в списке")    @allure.label("service", "Orders")    @allure.feature("Прайсрулы")    @allure.label("owner", "balakirevams")    @allure.severity(Severity.CRITICAL)    @allure.suite("CMS")    @allure.description("Проверяем удаление прайсрула и то, что он не выводится в списках")    def test_delete_pricerule(self):        with allure.step("Находим прайсрул со статусом 'pending'"):            pricerule_id = utils.get_pricerule_by_status(status="pending")        with allure.step("Удаляем прайсрул"):            delete_response = self.api_pricerules.delete(pricerule_id=pricerule_id)        with allure.step("Проверяем, что прайсрул успешно удален, статус код 204"):            assert delete_response.status_code == 204        with allure.step("Находим прайсрул со статусом 'running'"):            pricerule_id = utils.get_pricerule_by_status(status="running")        with allure.step("Удаляем прайсрул"):            delete_response = self.api_pricerules.delete(pricerule_id=pricerule_id)        with allure.step("Проверяем, что прайсрул не удалился, статус код 422"):            assert delete_response.status_code == 422        with allure.step("Находим прайсрул со статусом 'ended'"):            pricerule_id = utils.get_pricerule_by_status(status="ended")        with allure.step("Удаляем прайсрул"):            delete_response = self.api_pricerules.delete(pricerule_id=pricerule_id)        with allure.step("Проверяем, что прайсрул успешно удален, статус код 204"):            assert delete_response.status_code == 204